mapper.select:
SELECT idx AS idx_message , title , content , idx_receiver , idx_sender , send_date , idx_reply , (SELECT email FROM MEMBER WHERE idx = ms.idx_receiver) AS receiver_email , (SELECT username FROM MEMBER WHERE idx = ms.idx_receiver) AS receiver_username , (SELECT email FROM MEMBER WHERE idx = ms.idx_sender) AS sender_email , (SELECT username FROM MEMBER WHERE idx = ms.idx_sender) AS sender_username FROM MESSAGE ms
idx_receiver = #{idx_login}
idx_sender = #{idx_login}

mapper.insert:
INSERT INTO MESSAGE (
	idx_receiver
	, idx_sender
	, title
	, content

	, idx_reply

) VALUES (
	#{idx_receiver}
	, #{idx_login}
	, #{title}
	, #{content}

	, #{idx_reply}

)
