mapper.insert:
	INSERT INTO POST (
		  idx_member
		, title
		, content
		, upload_date
		, delete_yn
	) VALUES (
		  #{idx_member}
		, #{title}
		, #{content}
		, NOW()
		, 'N'
	)

        SELECT max(idx) FROM POST

INSERT INTO POST_KEYWORD (
	  idx_post
	, idx_keyword
) VALUES (
	  #{idx_post}
	, #{idx_keyword}
)

INSERT INTO POST_FILE (
	  idx_post
	, file_name
	, file_path
	, file_size
) VALUES (
	  #{idx_post}
	, #{file_name}
	, #{file_path}
	, #{file_size}
)
